mapper.update:
update user set
    status = 'DELETE', updated_at = NOW()
    where username = #{username}

UPDATE USER SET
    password = #{password}, updated_at = NOW()
    WHERE username = #{username}

UPDATE USER SET
    full_name = #{fullName}, updated_at = NOW()
    WHERE username = #{username}

UPDATE USER SET
    location = #{location}, updated_at = NOW()
    WHERE username = #{username}
